#include <iostream>
#include <cstdio>
#include <cstdlib>

using namespace std;

//递归
unsigned long fib(int n){
    if(n<=1) return n;
    else
        return fib(n-1)+fib(n-2);
}

unsigned long fib_b(int n){
    unsigned long a=0;
    unsigned long b=1,c;
    if(n<=1) return n;
    else{
        for(int i=2;i<=n;i++){
            c=a+b;
            a=b;
            b=c;
        }
        return c;
    }
}


int main(){
    int n;
    printf("您想输出斐波那契数列的前几个数？\n");
    scanf("%d",&n);

//    printf("递归输出前%d个数为：\n",n);
//    for(int i =0; i<n; i++){
//        cout<<fib(i)<<" ";
//    }
//    cout<<endl;

    printf("非递归输出前%d个数为：\n",n);
    for(int i =0; i<n; i++){
        cout<<fib_b(i)<<" ";
    }
    cout<<endl;
}
