#include <iostream>
#include <stdio.h>
#include <stdlib.h>
#include <string.h> //strlen()
#include<algorithm> //sort

#define MAXSTRLEN 1001
using namespace std;

int ans[MAXSTRLEN];

int destr(int i,int num,int len,char str[]){
    //printf("i:%d num:%d len:%d \n",i,num,len);
    if(i >= len) return num;//终止条件
    if(str[i]=='5') return destr(i+1,num,len,str);//以‘5’开头或者连续两个5
    int sum=0;
    while(str[i]!='5' && i<len){
        sum*=10;
        sum+=str[i]-'0';
        i++;
    }
    ans[num]=sum;
    return destr(i+1,num+1,len,str); //递归下一个分割串
}

void display(int ans[],int len){
    for(int i=0;i <len;i++){
        cout<<ans[i]<<" ";
    }
    cout<<endl;
}

int main(){
    char str[MAXSTRLEN];
    int len,numstr;
    printf("Please input a string('5' is the segmentation):\n");
    while(cin>>str){
        len=strlen(str);
        numstr=destr(0,0,len,str);//i是计数器计算遍历到第i个字符；num是分解到的字符串数目；
        sort(ans,ans+numstr);
        display(ans,numstr);
    }
}
