#include <iostream>

using namespace std;

static int b=3, c=3;
static int num[3];

typedef struct BinTreeNode
{
    int data;
    struct BinTreeNode *lchild, *rchild;
}*BinTree;

void CreateBinTree(BinTree & t)
{
    char a;
    cin >> a;
    t = new BinTreeNode;
    if(b!=c)
    {
        if((b==0&&c==2)||(c==0&&b==2))
            t->data = 1;
        else if((b==1&&c==0)||(c==1&&b==0))
            t->data = 2;
        else
            t->data = 0;
    }
    else
    {
        if(b==2)
            t->data = 1;
        else
            t->data = 2;
    }

    b=c=t->data;

    if(a=='2')
    {
        CreateBinTree(t->lchild);
        b=t->lchild->data;
        c=t->data;
        CreateBinTree(t->rchild);
    }
    else if(a=='1')
    {
        CreateBinTree(t->lchild);
        t->rchild = NULL;
    }
    else
    {
        t->lchild=NULL;
        t->rchild=NULL;
    }
}

void PreCount(BinTree t)
{
    if(t!=NULL)
    {
        num[t->data]++;
        PreCount(t->lchild);
        PreCount(t->rchild);
    }

}

int main()
{
    BinTree t;
    CreateBinTree(t);
    PreCount(t);
    int minC=num[0], maxC=num[0];
    for(int i=0; i<3; i++)
    {
        if(num[i]<minC)
            minC=num[i];
        else
            maxC=num[i];
    }
    cout << maxC << " " << minC << endl;
    return 0;
}
