#include "StuRec.h"
#include <iostream>
using namespace std;

void StuRec::CreateSR(int n)
{
    total=n;
    int i=1;
    do{
        cout<<"现在录入第"<<i<<"个学生的信息："<<endl;
        cout<<"学生姓名: ";
        cin>>sr[i].name;
        cout<<"性别: ";
        cin>>sr[i].sex;
        cout<<"学号: ";
        cin>>sr[i].num;
        cout<<"成绩1: ";
        cin>>sr[i].score1;
        cout<<"成绩2: ";
        cin>>sr[i].score2;
        sr[i].sum=sr[i].score1+sr[i].score2;
        i++;
    }while(i<=total);
}
void StuRec::SeqSearch()
{
    string name;
    int m=0;
    cout<<"请输入您要查找的姓名: ";
    cin>>name;
    for(int i=1; i<total; i++)
    {
        m=1;
        if(name==sr[i].name)
            cout<<sr[i].name<<" "<<sr[i].sex<<" "<<sr[i].num
            <<" "<<sr[i].score1<<" "<<sr[i].score2<<" "<<sr[i].sum<<endl;
    }
    if(m==0) cout<<"您所需要找的姓名不存在。"<<endl;
    else cout<<endl;
}

void StuRec::InsertSort()
{
    RecordNode stu;
    for(int i=2; i<=total; i++)
    {
        for(int j=i; j>1; j--)
        {
            if(sr[j].num<sr[j-1].num)
            {
                stu=sr[j];
                sr[j]=sr[j-1];
                sr[j-1]=stu;
            }
        }
    }
    for(int i=1; i<=total;i++)
        cout<<sr[i].name<<" "<<sr[i].sex<<" "<<sr[i].num
        <<" "<<sr[i].score1<<" "<<sr[i].score2<<" "<<sr[i].sum<<endl;
    cout<<endl;
}
void StuRec::SelectSort()
{
    RecordNode stu;
    for(int i=1; i<=total; i++)
    {
        for(int j=i+1; j<=total; j++)
        {
            if(sr[i].sum>sr[j].sum)
            {
                stu=sr[j];
                sr[j]=sr[i];
                sr[i]=stu;
            }
        }
    }
    for(int i=1; i<=total;i++)
        cout<<sr[i].name<<" "<<sr[i].sex<<" "<<sr[i].num
        <<" "<<sr[i].score1<<" "<<sr[i].score2<<" "<<sr[i].sum<<endl;
    cout<<endl;
}
