#include <iostream>
#include <cstdlib>
using namespace std;
int OddEvenSort(int s[], int n)
{
    int i, exchange = 1, temp, num = 0;
    while(exchange != 0){
        exchange = 0;
        for(i = 1; i < n - 1; i += 2){
            if(s[i] > s[i+1])
            {
                exchange = 1;
                temp = s[i];
                s[i] = s[i+1];
                s[i+1] = temp;
            }
        }
        if(exchange){
            num++;
            exchange = 0;
            for(int w = 0; w < n; w++)
                cout<<s[w]<<" ";
            cout<<endl;
        }
        for(i = 0; i < n - 1; i += 2){
            if(s[i] > s[i+1])
            {
                exchange = 1;
                temp = s[i];
                s[i] = s[i+1];
                s[i+1] = temp;
            }
        }
        if(exchange){
            num++;
            for(int t = 0; t < n; t++)
                cout<<s[t]<<" ";
            cout<<endl;
        }
    }
    return num;
}
int main()
{
    int n;
    cout<<"请输入数组位数:";
    cin>>n;
    int s[n];
    cout<<"请依次输入数组数据："<<endl;
    for(int i = 0; i < n; i++)
    {
        int number;
        cin>>number;
        s[i] = number;
    }
    cout<<"未排序前数组数据为: "<<endl;
    for(int i = 0; i < n; i++)
        cout<<s[i]<<" ";
    cout<<endl;
    cout<<"排序过程: "<<endl;
    int sum = OddEvenSort(s, n);
    cout<<"排序后数组数据为: "<<endl;
    for(int i = 0; i < n; i++)
        cout<<s[i]<<" ";
    cout<<endl;
    cout<<"总排序趟数："<<sum<<endl;
}
