#include<stdio.h>
#include<stdlib.h>
#include<iostream>


using namespace std;
//sequence list
#define ListSize 100      //×î´óÔÊÐí³¤¶È
typedef int KeyType;
typedef int ListData;
	typedef struct {
    ListData *data;      //´æ´¢¿Õ¼ä»ùÖ·
    int length; 	      //µ±Ç°ÔªËØ¸öÊý
}SeqList;

void list_initial(SeqList &l){
    l.data = ( ListData * ) malloc
           ( ListSize * sizeof ( ListData ) );
     if ( l.data == NULL ) {
         printf ( "´æ´¢·ÖÅäÊ§°Ü!\n" );
         exit (1);
     }
    // printf("11111111111111\n");
    l.length= 10;
    for(int i=1 ;i<= l.length; i++){
        l.data[i]=i;
        //printf("%d ",l.data[i]);
    }
    //printf("11111111111111\n");
}
int Search_Bin ( SeqList ST, KeyType kval ) {
    int low,mid,high;
   low = 1;  high = ST.length;     // ÖÃÇø¼ä³õÖµ
   printf("\nThe search index is: ");
   while (low <= high) {
      mid = (low + high) / 2;
      printf("%d ",mid);
      if (kval == ST.data[mid]  )
          return  mid;        // ÕÒµ½´ý²éÔªËØ
      else  if ( kval < ST.data[mid] )
        high = mid - 1;       // ¼ÌÐøÔÚÇ°°ëÇø¼ä½øÐÐ²éÕÒ
      else  low = mid + 1; // ¼ÌÐøÔÚºó°ëÇø¼ä½øÐÐ²éÕÒ
   }
   return 0;                 // Ë³Ðò±íÖÐ²»´æÔÚ´ý²éÔªËØ
} // Search_Bin
int main(){
    SeqList l;
    list_initial(l);
    printf("The list is:\n");
    for(int i=1 ;i<= l.length; i++){
        printf("%d ",l.data[i]);
    }
    KeyType key=9;
    ListData  x;
    x=Search_Bin(l,key);
    printf("\nThe index of the key %d is: %d\n",key,x);

    return 0;

}
