#include<stdio.h>
#include<stdlib.h>
#include<iostream>


using namespace std;
//sequence list
#define ListSize 100      //最大允许长度
typedef int KeyType;
typedef int ListData;
	typedef struct {
    ListData *data;      //存储空间基址
    int length; 	      //当前元素个数
}SeqList;

void list_initial(SeqList &l){
    l.data = ( ListData * ) malloc
           ( ListSize * sizeof ( ListData ) );
     if ( l.data == NULL ) {
         printf ( "存储分配失败!\n" );
         exit (1);
     }
    // printf("11111111111111\n");
    l.length= 10;
    for(int i=1 ;i<= l.length; i++){
        l.data[i]=i;
        //printf("%d ",l.data[i]);
    }
    //printf("11111111111111\n");
}
int Search_Bin ( SeqList ST, KeyType kval ) {
    int low,mid,high;
   low = 1;  high = ST.length;     // 置区间初值
   printf("\nThe search index is: ");
   while (low <= high) {
      mid = (low + high) / 2;
      printf("%d ",mid);
      if (kval == ST.data[mid]  )
          return  mid;        // 找到待查元素
      else  if ( kval < ST.data[mid] )
        high = mid - 1;       // 继续在前半区间进行查找
      else  low = mid + 1; // 继续在后半区间进行查找
   }
   return 0;                 // 顺序表中不存在待查元素
} // Search_Bin
int main(){
    SeqList l;
    list_initial(l);
    printf("The list is:\n");
    for(int i=1 ;i<= l.length; i++){
        printf("%d ",l.data[i]);
    }
    KeyType key=9;
    ListData  x;
    x=Search_Bin(l,key);
    printf("\nThe index of the key %d is: %d\n",key,x);

    return 0;

}
